import React, { useRef, useState } from "react";
import { Canvas, useFrame } from "react-three-fiber";
import { MeshWobbleMaterial } from "drei";
import "./App.css";
import { useSpring, a } from "react-spring/three";
import { SketchPicker } from "react-color";
import { Navbar, Nav } from "react-bootstrap";




const SpinningMesh = ({ position, color, speed, args }) => {
  const mesh = useRef();

  useFrame(() => (mesh.current.rotation.x = mesh.current.rotation.y += 0.01));

  const [expand, setExpand] = useState(false);
  const props = useSpring({
    scale: expand ? [1.4, 1.4, 1.4] : [1, 1, 1],
  });
  console.log(expand);
  return (

    <a.mesh
      position={position}
      ref={mesh}
      onClick={() => setExpand(!expand)}
      scale={props.scale}
      castShadow>
      <boxBufferGeometry attach='geometry' args={args}  />
      <MeshWobbleMaterial
        color={color}
        speed={speed}
        attach='material'
        factor={0.6}
      />
    </a.mesh>

  );
};

const App = () => {
  const [usecolor, setUsecolor] = React.useState({
    colorName:""
  });
  const {colorName }=usecolor;
  const onInputChange= e =>{
    setUsecolor({[e.target.name]:e.target.value})

};
console.log(colorName);

  return (
    <>
   <SketchPicker />;
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter New Category"
              name="colorName"
              value={colorName}
              onChange={e => onInputChange(e)}
              required
            /> <div>
            <Navbar bg="light" expand="lg">
                <div className="container">
                    <Link className="navbar-brand" exact to="/"><img className="App-logo" src={logo} alt="logo" /></Link>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">
                            <NavLink className="nav-link" exact to="/">Home</NavLink>
                            <NavLink className="nav-link" exact to="/category">Category</NavLink>
                            <NavLink className="nav-link" exact to="/product">Product</NavLink>
                        </Nav>

                        <Link className="btn btn-outline" exact to="/auth/add"> Add Category </Link>
                        <Link className="btn btn-outline" exact to="/auth/addproduct"> Add Product </Link>

                    </Navbar.Collapse>
                </div>
            </Navbar>




        </div>



      {/* Our Scene & Camera is already built into our canvas */}
      <Canvas
        colorManagement
        camera={{ position: [-50, 2, 10], fov: 60 }}>

        <ambientLight intensity={0.3} />

        <group>      
          <SpinningMesh
            position={[0, 1, 0]}
            color={colorName}
            args={[5, 5, 5]}
            speed={2}
          />

        </group>

      </Canvas>
    </>
  );
};

export default App;
